function updateGermanyClock() {
    const now = new Date();
    const germanyTime = new Date(now.toLocaleString('en-US', { timeZone: 'Europe/Berlin' }));

    const hours = germanyTime.getHours();
    const minutes = germanyTime.getMinutes();
    const seconds = germanyTime.getSeconds();

    const hourHand = root_element.querySelector('#germany-hour');
    const minuteHand = root_element.querySelector('#germany-minute');
    const secondHand = root_element.querySelector('#germany-second');

    if (hourHand) {
        const hourDeg = (hours % 12) * 30 + minutes * 0.5;
        hourHand.style.transform = `translateX(-50%) rotate(${hourDeg}deg)`;
    }

    if (minuteHand) {
        const minuteDeg = minutes * 6;
        minuteHand.style.transform = `translateX(-50%) rotate(${minuteDeg}deg)`;
    }

    if (secondHand) {
        const secondDeg = seconds * 6;
        secondHand.style.transform = `translateX(-50%) rotate(${secondDeg}deg)`;
    }
}

setInterval(updateGermanyClock, 1000);
updateGermanyClock();

