script:
const textarea = root_element.querySelector('#apex-notes-area');
const saveBtn = root_element.querySelector('#save-notes-btn');
const clearBtn = root_element.querySelector('#clear-notes-btn');

// Get current user ID
const userId = frappe.session.user;
const storageKey = `apex_workspace_notes_${userId}`;

// Load saved notes
if (textarea) {
    const savedNotes = localStorage.getItem(storageKey) || '';
    textarea.value = savedNotes;

    // CRITICAL: Stop all keyboard events from propagating
    textarea.addEventListener('keydown', function(e) {
        e.stopPropagation();
        e.stopImmediatePropagation();
    }, true);

    textarea.addEventListener('keyup', function(e) {
        e.stopPropagation();
        e.stopImmediatePropagation();
    }, true);

    textarea.addEventListener('keypress', function(e) {
        e.stopPropagation();
        e.stopImmediatePropagation();
    }, true);

    // Auto-save on input
    let saveTimeout;
    textarea.addEventListener('input', function() {
        clearTimeout(saveTimeout);
        saveTimeout = setTimeout(function() {
            localStorage.setItem(storageKey, textarea.value);
        }, 1000);
    });
}

// Save button
if (saveBtn) {
    saveBtn.addEventListener('click', function() {
        if (textarea) {
            localStorage.setItem(storageKey, textarea.value);
            frappe.show_alert({message: 'Notes saved!', indicator: 'green'}, 2);
        }
    });
}

// Clear button
if (clearBtn) {
    clearBtn.addEventListener('click', function() {
        if (confirm('Clear all notes?')) {
            if (textarea) {
                textarea.value = '';
                localStorage.removeItem(storageKey);
                frappe.show_alert({message: 'Notes cleared!', indicator: 'blue'}, 2);
            }
        }
    });
}

