script:
const hourHand = root_element.querySelector('#hour-hand');
const minuteHand = root_element.querySelector('#minute-hand');
const secondHand = root_element.querySelector('#second-hand');

// Draw minute markers
const marksGroup = root_element.querySelector('#minute-marks');
if (marksGroup) {
    for (let i = 0; i < 60; i++) {
        const angle = i * 6;
        const isHourMark = i % 5 === 0;
        const length = isHourMark ? 8 : 4;
        const width = isHourMark ? 2 : 1;

        const line = document.createElementNS('http://www.w3.org/2000/svg', 'line');
        line.setAttribute('x1', '100');
        line.setAttribute('y1', '10');
        line.setAttribute('x2', '100');
        line.setAttribute('y2', String(10 + length));
        line.setAttribute('stroke', '#333');
        line.setAttribute('stroke-width', String(width));
        line.setAttribute('transform', `rotate(${angle} 100 100)`);

        marksGroup.appendChild(line);
    }
}

function updateAnalog() {
    const now = new Date();
    const seconds = now.getSeconds();
    const minutes = now.getMinutes();
    const hours = now.getHours();

    const secondDegrees = ((seconds / 60) * 360);
    const minuteDegrees = ((minutes / 60) * 360) + ((seconds/60)*6);
    const hourDegrees = ((hours / 12) * 360) + ((minutes/60)*30);

    if(secondHand) secondHand.style.transform = `rotate(${secondDegrees}deg)`;
    if(minuteHand) minuteHand.style.transform = `rotate(${minuteDegrees}deg)`;
    if(hourHand) hourHand.style.transform = `rotate(${hourDegrees}deg)`;
}

setInterval(updateAnalog, 1000);
updateAnalog();

