script:
// Draw clock markers
const markersGroup = root_element.querySelector('#china-markers');
if (markersGroup && !markersGroup.hasChildNodes()) {
    for (let i = 0; i < 60; i++) {
        const angle = i * 6;
        const isHourMark = i % 5 === 0;
        const length = isHourMark ? 10 : 5;
        const width = isHourMark ? 2 : 1;
        const line = document.createElementNS('http://www.w3.org/2000/svg', 'line');
        line.setAttribute('x1', '100');
        line.setAttribute('y1', '5');
        line.setAttribute('x2', '100');
        line.setAttribute('y2', String(5 + length));
        line.setAttribute('stroke', '#1F272E');
        line.setAttribute('stroke-width', String(width));
        line.setAttribute('transform', `rotate(${angle} 100 100)`);
        markersGroup.appendChild(line);
    }
}

function updateChinaClock() {
    const now = new Date();
    const chinaTime = new Date(now.toLocaleString('en-US', { timeZone: 'Asia/Shanghai' }));

    const hours = chinaTime.getHours();
    const minutes = chinaTime.getMinutes();
    const seconds = chinaTime.getSeconds();

    const hourHand = root_element.querySelector('#china-hour');
    const minuteHand = root_element.querySelector('#china-minute');
    const secondHand = root_element.querySelector('#china-second');

    if (hourHand) {
        const hourDeg = (hours % 12) * 30 + minutes * 0.5;
        hourHand.style.transform = `translateX(-50%) rotate(${hourDeg}deg)`;
    }

    if (minuteHand) {
        minuteHand.style.transform = `translateX(-50%) rotate(${minutes * 6}deg)`;
    }

    if (secondHand) {
        secondHand.style.transform = `translateX(-50%) rotate(${seconds * 6}deg)`;
    }
}

setInterval(updateChinaClock, 1000);
updateChinaClock();

