script:
const userId = frappe.session.user;
const textarea = root_element.querySelector('#user-notes');
const saveBtn = root_element.querySelector('#save-notes');
const clearBtn = root_element.querySelector('#clear-notes');

// Load notes from server
function loadNotes() {
    frappe.call({
        method: 'frappe.client.get_value',
        args: {
            doctype: 'User',
            filters: { name: userId },
            fieldname: 'bio'
        },
        callback: function(r) {
            if (r.message && textarea) {
                textarea.value = r.message.bio || '';
            }
        }
    });
}

// Save notes to server
function saveNotes() {
    if (!textarea) return;

    frappe.call({
        method: 'frappe.client.set_value',
        args: {
            doctype: 'User',
            name: userId,
            fieldname: 'bio',
            value: textarea.value
        },
        callback: function(r) {
            if (r.message) {
                frappe.show_alert({message: '‚úÖ Notes saved and synced!', indicator: 'green'}, 3);
            }
        },
        error: function(err) {
            frappe.show_alert({message: '‚ùå Error saving notes', indicator: 'red'}, 3);
            console.error('Save error:', err);
        }
    });
}

// Clear notes
function clearNotes() {
    if (!confirm('Clear all notes?')) return;

    if (textarea) textarea.value = '';

    frappe.call({
        method: 'frappe.client.set_value',
        args: {
            doctype: 'User',
            name: userId,
            fieldname: 'bio',
            value: ''
        },
        callback: function(r) {
            frappe.show_alert({message: 'üóëÔ∏è Notes cleared!', indicator: 'blue'}, 2);
        }
    });
}

// Event listeners
if (textarea) {
    // Stop propagation to prevent global search
    textarea.addEventListener('keydown', (e) => e.stopPropagation(), true);
    textarea.addEventListener('keyup', (e) => e.stopPropagation(), true);
    textarea.addEventListener('keypress', (e) => e.stopPropagation(), true);

    // Auto-save every 5 seconds if changed
    let lastValue = '';
    let saveTimeout;
    textarea.addEventListener('input', () => {
        clearTimeout(saveTimeout);
        saveTimeout = setTimeout(() => {
            if (textarea.value !== lastValue) {
                saveNotes();
                lastValue = textarea.value;
            }
        }, 5000);
    });
}

if (saveBtn) {
    saveBtn.addEventListener('click', saveNotes);
}

if (clearBtn) {
    clearBtn.addEventListener('click', clearNotes);
}

// Load notes on init
loadNotes();

