setTimeout(function() {
    // Update date
    const dateEl = root_element.querySelector('#weather-date');
    if (dateEl) {
        const now = new Date();
        const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
        dateEl.textContent = now.toLocaleDateString('en-US', options);
    }

    // Fetch weather data from free API (wttr.in)
