        .then(response => response.json())
        .then(data => {
            const current = data.current_condition[0];
            const today = data.weather[0];

            const tempEl = root_element.querySelector('#weather-temp');
            const conditionEl = root_element.querySelector('#weather-condition');
            const rangeEl = root_element.querySelector('#weather-range');

            if (tempEl) tempEl.textContent = current.temp_C + '¬∞C';
