            if (rangeEl) rangeEl.textContent = today.mintempC + '¬∞C / ' + today.maxtempC + '¬∞C';
        })
        .catch(error => {
            console.log('Weather data not available, using default');
            // Fallback to static data
            const tempEl = root_element.querySelector('#weather-temp');
            const conditionEl = root_element.querySelector('#weather-condition');
            const rangeEl = root_element.querySelector('#weather-range');

            if (tempEl) tempEl.textContent = '25¬∞C';
            if (conditionEl) conditionEl.textContent = 'Sunny';
