.clock-number::after {
    content: attr(data-number);
    position: absolute;
    top: 10%;
    left: 50%;
    transform: translateX(-50%);
}
.clock-number:nth-of-type(1) { transform: rotate(0deg); }
.clock-number:nth-of-type(1)::after { transform: translateX(-50%) rotate(0deg); }
