script:
const holidays = [
    { name: "New Year's Day", date: "January 1, 2026" },
    { name: "Coptic Christmas", date: "January 7, 2026" },
    { name: "Revolution Day", date: "January 25, 2026" },
    { name: "Eid al-Fitr", date: "March 20-22, 2026" },
    { name: "Sinai Liberation Day", date: "April 25, 2026" },
    { name: "Labour Day", date: "May 1, 2026" },
    { name: "Eid al-Adha", date: "May 26-30, 2026" },
    { name: "Revolution Day", date: "July 23, 2026" }
];

let currentIndex = 0;

function updateHoliday() {
    const nameEl = root_element.querySelector('#holiday-name');
    const dateEl = root_element.querySelector('#holiday-date');

    if (nameEl) nameEl.textContent = holidays[currentIndex].name;
    if (dateEl) dateEl.textContent = holidays[currentIndex].date;
}

const prevBtn = root_element.querySelector('#prev-holiday');
const nextBtn = root_element.querySelector('#next-holiday');

if (prevBtn) {
    prevBtn.addEventListener('click', () => {
        currentIndex = (currentIndex - 1 + holidays.length) % holidays.length;
        updateHoliday();
    });
}

if (nextBtn) {
    nextBtn.addEventListener('click', () => {
        currentIndex = (currentIndex + 1) % holidays.length;
        updateHoliday();
    });
}

updateHoliday();

