script:
function updateGermanyClock() {
    const now = new Date();
    const germanyTime = new Date(now.toLocaleString('en-US', { timeZone: 'Europe/Berlin' }));

    const hours = germanyTime.getHours();
    const minutes = germanyTime.getMinutes();
    const seconds = germanyTime.getSeconds();

    const hourHand = root_element.querySelector('#germany-hour');
    const minuteHand = root_element.querySelector('#germany-minute');
    const secondHand = root_element.querySelector('#germany-second');

    if (hourHand) {
        const hourDeg = (hours % 12) * 30 + minutes * 0.5;
        hourHand.style.transform = `translateX(-50%) rotate(${hourDeg}deg)`;
    }

    if (minuteHand) {
        minuteHand.style.transform = `translateX(-50%) rotate(${minutes * 6}deg)`;
    }

    if (secondHand) {
        secondHand.style.transform = `translateX(-50%) rotate(${seconds * 6}deg)`;
    }
}

setInterval(updateGermanyClock, 1000);
updateGermanyClock();

style:
.analog-clock {
    width: 180px;
    height: 180px;
    margin: 15px auto;
    position: relative;
}
.clock-face {
    width: 100%;
    height: 100%;
    border-radius: 50%;
    border: 6px solid #1F272E;
    background: white;
    position: relative;
}
.hand {
    position: absolute;
    bottom: 50%;
    left: 50%;
    transform-origin: bottom center;
    background: #1F272E;
    border-radius: 2px;
}
.hour-hand {
    width: 4px;
    height: 50px;
    transform: translateX(-50%) rotate(0deg);
}
.minute-hand {
    width: 3px;
    height: 70px;
    background: #4C9AFF;
    transform: translateX(-50%) rotate(0deg);
}
.second-hand {
    width: 2px;
    height: 75px;
    background: #FF5630;
    transform: translateX(-50%) rotate(0deg);
}
.center-dot {
    position: absolute;
    width: 12px;
    height: 12px;
    background: #1F272E;
    border-radius: 50%;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    z-index: 10;
}

